package com.comit.practice_hw;

public class AssignValuesToVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*Write a Java program that does the following: declare a variable N of type int, 
 		variable A of type double and a variable C of type char and assign to each one a value.
 		 The following screen displays:
		The value of each variable. The sum of N + A. And A - N */
		
		int N = 10;
		double A = 12.50;
		char C = 'A';
		
		System.out.println("N : "+N);
		System.out.println("A : "+A);
		System.out.println("C : "+C);
		
		System.out.println("N+A : " +(N+A));
		System.out.println("A-N : " +(A-N));

	}

}
