package com.comit.practice_hw;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Write a java program that declares an integer variable B and assign it a
		 * value. It then displays a message indicating whether the value of B is
		 * positive or negative. We will consider 0 as positive. If for example B = 1
		 * the output will be 1 is positive. If for example B = -1 the output will be:
		 * -1 is negative
		 */

		int B = -2;

		if (B >= 0) {
			System.out.println(+B + " is positive");
		} else {
			System.out.println(+B + " is negative");
		}

	}

}
